import React, { useEffect, useState } from "react";

const data = {
    id: 1,
    example: 'ok',
    answer: 'spoko'
}

export default function WriteRightText() {
    const [value, setValue] = useState('')
    const [isRight, setIsRight] = useState('empty')

    useEffect(() => {
        if (value === data.answer) {
            setIsRight('correct')
        }

        if (value !== data.answer) {
            setIsRight('incorrect')
        }

        if (value !== '') {
            setIsRight('empty')
        }
    }, [value])



    return (
        <div className="WriteRightText">
            <div className="WriteRightText__example">
                <p>{data.example}</p>
            </div>

            <div className="WriteRightText__block">
                <input
                    value={value}
                    type="text"
                    onChange={(e) => setValue(e.target.value)}
                />

                <div className={
                    (isRight === 'correct' && 'WriteRightText__answer WriteRightText__answer--correct')
                    || (isRight === 'incorrect' && 'WriteRightText__answer WriteRightText__answer--incorrect')
                    || (isRight === 'empty' && null)
                }
                >
                    {
                        (isRight === 'correct' && 'отлично!')
                        || (isRight === 'incorrect' && `${data.answer}`)
                        || (isRight === 'empty' && null)
                    }
                </div>
            </div>

        </div>
    )
}
