import React, { useEffect, useState } from "react";
import ExDescription from "./ExDescription";

export default function WriteRightText({ wrtExs }) {

    const [value, setValue] = useState('')
    const [isRight, setIsRight] = useState(false);
    const [currentClass, setCurrentClass] = useState('')

    const isEmpty = !value.length;

    useEffect(() => {
        setIsRight(value === wrtExs.answer);
        isEmpty && setCurrentClass('WriteRightText__answer--hidden');
    }, [value, isEmpty]);

    function onClickHandler() {
        if (isEmpty) return 0;
        setCurrentClass('WriteRightText__answer--' + (isRight ? 'correct' : 'incorrect'));
    }

    function renderElements() {
        return wrtExs.map(v => {
            return (
                <div className="WriteRightText__element">

                    <div className="WriteRightText__example">
                        <p>{v.example}</p>
                    </div>

                    <div className="WriteRightText__block">
                        <input
                            value={value}
                            type="text"
                            onChange={(e) => setValue(e.target.value)}
                        />

                        <div className={`WriteRightText__answer ${currentClass}`}>{isRight ? 'Все верно!' : `Правильный ответ: ${v.answer}`}</div>
                    </div>

                    <button
                        type="button"
                        onClick={() => onClickHandler()}
                    >
                        <span>Проверить</span>
                    </button>
                </div>
            )
        })


    }

    return (
        <div className="WriteRightText">
            {wrtExs.length > 0 && renderElements()}
        </div>
    )
}
