import React, { useEffect, useState } from "react";

const data = {
    id: 1,
    example: 'яблоко',
    answer: 'jabłko'
}

export default function WriteRightText() {
    const [value, setValue] = useState('')
    const [isRight, setIsRight] = useState(false)
    const [isClear, setIsClear] = useState(true)
    const [currentClass, setCurrentClass] = useState('')

    useEffect(() => {
        value === data.answer ? setIsRight(true) : setIsRight(false)
        value === '' ? setIsClear(true) : setIsClear(false)
        isClear && setCurrentClass('WriteRightText__answer--hidden')
    }, [value, isClear])

    console.log(value, isClear);

    function onClickHandler() {
        (isRight && !isClear) && setCurrentClass('WriteRightText__answer--correct')
        (!isRight && !isClear) && setCurrentClass('WriteRightText__answer--incorrect')
    }

    return (
        <div className="WriteRightText">
            <div className="WriteRightText__example">
                <p>{data.example}</p>
            </div>

            <div className="WriteRightText__block">
                <input
                    value={value}
                    type="text"
                    onChange={(e) => setValue(e.target.value)}
                />

                <div className={`WriteRightText__answer ${currentClass}`}>{isRight ? 'Все верно!' : data.answer}</div>
            </div>

            <button
                type="button"
                onClick={() => onClickHandler()}
            >
                Проверить
            </button>
        </div>
    )
}
