import React, { useEffect, useState } from "react";

const data = {
    id: 1,
    example: 'ok',
    answer: 'spoko'
}

export default function WriteRightText() {
    const [value, setValue] = useState('')
    const [isRight, setIsRight] = useState(false)
    const [isClear, setIsClear] = useState(true)

    useEffect(() => {
        value === data.answer ? setIsRight(true) : setIsRight(false)
        value === '' ? setIsClear(true) : setIsClear(false)
    }, [value])

    return (
        <div className="WriteRightText">
            <div className="WriteRightText__example">
                <p>{data.example}</p>
            </div>

            <div className="WriteRightText__block">
                <input
                    value={value}
                    type="text"
                    onChange={(e) => setValue(e.target.value)}
                />

                {(isRight && !isClear) && <div className="WriteRightText__answer WriteRightText__answer--correct">Good!</div>}
                {isClear && null}
                {(!isRight && !isClear) && <div className="WriteRightText__answer WriteRightText__answer--incorrect">{data.answer}</div>}

            </div>

        </div>
    )
}
